import { Component, OnInit } from '@angular/core';
import { UserService } from '../_services/user.service';

import Web3 from 'web3';
import { AbiItem } from 'web3-utils'

// deteethproviderfix dont care how do it  
@Component({
  selector: 'app-board-user',
  templateUrl: './board-user.component.html',
  styleUrls: ['./board-user.component.css']
})
export class BoardUserComponent implements OnInit {
  content?: string;
  depositForm: any = {
    ethValue: null,
  };
  private web3: Web3;
  public walletConnected: boolean = false;
  public walletId: string = '';

  constructor(private userService: UserService,
  ) { 
    const alchemy_url = "https://eth-sepolia.g.alchemy.com/v2/YGNAUM7D8wTvwFUIV8iyRv0i8N6BTT86"
    this.web3 = new Web3(alchemy_url);

  }

  ngOnInit(): void {
    this.userService.getUserBoard().subscribe({
      next: data => {
        this.content = data;
      },
      error: err => {
        if (err.error) {
          try {
            const res = JSON.parse(err.error);
            this.content = res.message;
          } catch {
            this.content = `Error with status: ${err.status} - ${err.statusText}`;
          }
        } else {
          this.content = `Error with status: ${err.status}`;
        }
      }
    });
  }

  connectToWallet  = () => {
    this.userService.connectWallet();
  }
  checkWalletConnected = async () => {
    const accounts = await this.userService.checkWalletConnected();
    if(accounts.length > 0){
      this.walletConnected = true;
      this.walletId = accounts[0];
      console.log(this.walletId);
    }
  }
 async getContractBalance(){

    let contractInst = new this.web3.eth.Contract(this.userService.abiContract as AbiItem[],this.userService.contractAddress);
    let contractBalance = contractInst.methods.getContractBalance()
    .call();
    return contractBalance;
  }

  async onSubmit(): Promise<void> {
    this.connectToWallet();
    this.checkWalletConnected();
    let balance;
    balance = this.getContractBalance().then(res=>{console.log(this.web3.utils.fromWei(res, "ether"),"eth")});
    console.log(this.depositForm);
    //console.log(Web3.version);
    console.log(this.walletId);
    var accounts = await this.web3.eth.getAccounts();
   let balance2 = await this.web3.eth.getBalance(this.walletId).then(console.log);
    //console.log(this.web3.utils.fromWei(await balance2, "ether"));
    //const transaction006=this.web3.utils.toWei("0.1","ether");
    //let txResult=await contractInst.methods.deposit().send({from: accounts[0],value:transaction006});
    /*let txResult2=await contractInst.methods.
    withdrawToOther(transaction006, "0xe1e481Ba1AD79E5d644440847eeabb139eA3Bf06").
    send({from: accounts[0]});
    */
    //console.log(txResult2);

  }

}
<div class="container">
  <header class="jumbotron">
    <form class="form-group"     
    name="depositForm"
    #f="ngForm" 
    (ngSubmit)="onSubmit()">

      <label for="ethValue">Value:</label>
      <input type="number" class="ethValue"           
      [(ngModel)]="depositForm.ethValue"
      name="ethValue"
      >    
      <button class="btn btn-primary btn-block">Deposit here</button>
    </form>
  </header>
</div>
